const convertirCodigo=()=>{const e=document.getElementById("CodigoJava").value.trim();if(!e)return void alert("El campo no puede estar vacío. Por favor, ingrese código Java.");if(!e.includes(";"))return void alert('El código ingresado no parece ser válido. Asegúrese de incluir instrucciones terminadas con ";"');const t=e.replace(/\b(int|String|boolean|float|double)\b/g,"let").replace(/System\.out\.println\((.*?)\);/g,"console.log($1);").replace(/\bpublic static void main\(String\[\] args\)/g,"function main()").replace(/\b(int|float|double|boolean|String)\[\]\s+(\w+)\s*=\s*new\s+\1\[(\d+)\];/g,"let $2 = new Array($3);").replace(/\b(int|float|double|boolean|String)\[\]\s+(\w+)\s*=\s*\{([^\}]+)\};/g,"let $2 = [$3];").replace(/for\s*\((.*?)\)/g,"for ($1)");document.getElementById("ResultadoJS").textContent=t},pegarTexto=async()=>{try{const e=await navigator.clipboard.readText();document.getElementById("CodigoJava").value=e}catch(e){alert("Error al acceder al portapapeles: "+e.message)}},copiarTexto=async()=>{try{const e=document.getElementById("ResultadoJS").textContent;await navigator.clipboard.writeText(e);const t=document.getElementById("alertaTexto");t.style.display="block",setTimeout(()=>t.style.display="none",2e3)}catch(e){alert("Error al copiar al portapapeles: "+e.message)}},limpiarCampos=()=>{document.getElementById("CodigoJava").value="",document.getElementById("ResultadoJS").textContent=""};document.getElementById("convertirCodigo").addEventListener("click",convertirCodigo),document.getElementById("pegarTexto").addEventListener("click",pegarTexto),document.getElementById("copiarTexto").addEventListener("click",copiarTexto),document.getElementById("limpiarCampos").addEventListener("click",limpiarCampos);