const convertirCodigo=()=>{const e=document.getElementById("CodigoJava").value.trim();if(!e)return alert("El campo no puede estar vacío. Por favor, ingrese código Java.");if(!e.includes(";"))return alert('El código ingresado no parece ser válido. Asegúrese de incluir instrucciones terminadas con ";"');const t=[{regex:/\b(int|String|boolean|float|double)\b/g,reemplazo:"let"},{regex:/System\.out\.println\((.*?)\);/g,reemplazo:"console.log($1);"},{regex:/\bpublic static void main\(String\[\] args\)/g,reemplazo:"function main()"},{regex:/\b(int|float|double|boolean|String)\[\]\s+(\w+)\s*=\s*new\s+\1\[(\d+)\];/g,reemplazo:"let $2 = new Array($3);"},{regex:/\b(int|float|double|boolean|String)\[\]\s+(\w+)\s*=\s*\{([^\}]+)\};/g,reemplazo:"let $2 = [$3];"},{regex:/for\s*\((.*?)\)/g,reemplazo:"for ($1)"}],o=t.reduce((e,{regex:t,reemplazo:o})=>e.replace(t,o),e);document.getElementById("ResultadoJS").textContent=o},pegarTexto=async()=>{try{const e=await navigator.clipboard.readText();document.getElementById("CodigoJava").value=e}catch(e){alert(`Error al acceder al portapapeles: ${e.message}`)}},copiarTexto=async()=>{try{const e=document.getElementById("ResultadoJS").textContent;await navigator.clipboard.writeText(e),mostrarAlerta("Texto copiado al portapapeles.")}catch(e){alert(`Error al copiar al portapapeles: ${e.message}`)}},limpiarCampos=()=>{document.getElementById("CodigoJava").value="",document.getElementById("ResultadoJS").textContent=""},mostrarAlerta=e=>{const t=document.getElementById("alertaTexto");t.textContent=e,t.style.display="block",setTimeout(()=>t.style.display="none",2e3)};document.getElementById("convertirCodigo").addEventListener("click",convertirCodigo),document.getElementById("pegarTexto").addEventListener("click",pegarTexto),document.getElementById("copiarTexto").addEventListener("click",copiarTexto),document.getElementById("limpiarCampos").addEventListener("click",limpiarCampos);